INSERT INTO cdb_templates VALUES('1','header','0','<html>\n<head>\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=$charset\\\">\n<meta name=\\\"generator\\\" content=\\\"CDB $version with Templates 3.0 RC1\\\">\n<meta name=\\\"keywords\\\" content=\\\"$bbname,Discuz,Board,Crossday Studio,PHP,MySQL,FORUM\\\">\n<meta name=\\\"description\\\" content=\\\"$bbname - Powered by Discuz! Board $version\\\">\n<meta name=\\\"MSSmartTagsPreventParsing\\\" content=\\\"TRUE\\\">\n<meta http-equiv=\\\"MSThemeCompatible\\\" content=\\\"Yes\\\">\n$url_redirect\n\n<title>$bbname$navtitle</title>\n$css\n<script language=\\\"JavaScript\\\">\nfunction Popup(url, window_name, window_width, window_height) {settings=\\\"toolbar=no,location=no,directories=no,\\\"+\\\"status=no,menubar=no,scrollbars=yes,\\\"+\\\"resizable=yes,width=\\\"+window_width+\\\",height=\\\"+window_height;NewWindow=window.open(url,window_name,settings);}\nfunction ctlent(obj) {if((event.ctrlKey && window.event.keyCode == 13) || (event.altKey && window.event.keyCode == 83)) {if(validate(this.document.input)) {this.document.input.submit();}}}\nfunction checkall(form) {for(var i = 0;i < form.elements.length; i++) {var e = form.elements[i];if (e.name != \\\'chkall\\\') e.checked = form.chkall.checked;}}\nfunction findobj(n, d) {var p,i,x; if(!d) d=document; if((p=n.indexOf(\\\"?\\\"))>0&&parent.frames.length) {d=parent.frames[n.substring(p+1)].document; n=n.substring(0,p);} if(!(x=d[n])&&d.all) x=d.all[n]; for(i=0;!x&&i<d.forms.length;i++) x=d.forms[i][n]; for(i=0;!x&&d.layers&&i>d.layers.length;i++) x=MM_findObj(n,d.layers[i].document); return x;}\nfunction copycode(obj) {var rng = document.body.createTextRange();rng.moveToElementText(obj);rng.scrollIntoView();rng.select();rng.execCommand(\\\"Copy\\\");rng.collapse(false);}\n</script>\n\n</head>\n<body>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\">\n<tr><td class=\\\"header\\\"><table width=\\\"100%\\\" cellpadding=\\\"0\\\" cellspacing=\\\"0\\\">\n<tr><td class=\\\"navtd\\\"><span class=\\\"bold\\\">&raquo;</span> $welcome$reglink$loginout$searchlink$memlistlink$memolink$statslink$u2ulink$pluglink$memcplink$cplink$faqlink\n</td><td align=\\\"right\\\"><a href=\\\"$siteurl\\\"><font class=\\\"navtd\\\">返回 $sitename</font></a>\n</td></tr></table></td></tr></table></td></tr></table><br>\n\n<table border=\\\"0\\\" width=\\\"$tablewidth\\\" cellpadding=\\\"0\\\" cellspacing=\\\"0\\\" align=\\\"center\\\">\n<tr><td align=\\\"left\\\" valign=\\\"top\\\" rowspan=\\\"2\\\"><a href=\\\"index.php\\\">$boardlogo</a><br></td>\n<td align=\\\"right\\\" valign=\\\"top\\\"><font class=\\\"mediumtxt\\\">$lastvisittext<br></font></tr>\n<tr>\n<td align=\\\"right\\\" valign=\\\"bottom\\\" class=\\\"mediumtxt\\\">$header_welcome</td></tr></table>\n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\" style=\\\"table-layout: fixed\\\"> \n<tr><td class=\\\"nav\\\" width=\\\"90%\\\" nowrap>&nbsp;<a href=\\\"index.php\\\">$bbname</a> $navigation</td>\n<td align=\\\"right\\\" width=\\\"10%\\\"><a href=\\\"#bottom\\\"><img src=\\\"$imgdir/arrow_dw.gif\\\" border=\\\"0\\\" align=\\\"absmiddle\\\"></a></td>        \n</tr></table><br>');
INSERT INTO cdb_templates VALUES('2','css','0','<style type=\\\"text/css\\\">\na:link,a:visited	{ text-decoration: none; color: $link }\na:hover			{ text-decoration: underline }\nselect			{ font-family: 宋体; font-size: $fontsize; font-weight: normal; background-color: $altbg1; color: $tabletext }\nbody			{ scrollbar-base-color: $altbg1; scrollbar-arrow-color: $bordercolor; font-size: $fontsize; $bgcode }\ntable			{ font-family: $font; color: $tabletext; font-size: $fontsize }\ntextarea,input,object	{ font-family: $font; font-size: $fontsize; font-weight: normal; background-color: $altbg1; color: $tabletext }\n.bold			{ font-weight: $bold }\n.subject		{ font-size: $fontsize; color: $tabletext; font-family: $font; font-weight: $bold }\n.post			{ font-size: $font3; font-weight: normal; font-family: $font }\n.header			{ color: $headertext; font-family: $font; font-weight: $bold; font-size: $fontsize; $headerbgcode }\n.category		{ font-family: $font; color: $cattext; font-size: $fontsize; $catbgcode }\n.nav			{ font-family: $font; font-weight: $bold; font-size: $fontsize }\n.smalltxt		{ font-size: 12px; color: $tabletext; font-family: Tohoma, Arial, Verdana }\n.mediumtxt		{ font-size: $fontsize; font-family: $font; font-weight: normal; color: $text }\n.navtd			{ font-size: $fontsize; font-family: $font; color: $headertext; text-decoration: none }\n.multi			{ font-family: $font; color: $link; font-size: $fontsize }\n</style>');
