INSERT INTO cdb_templates VALUES('35','viewthread_poll','0','<form method=\\\"post\\\" action=\\\"topicadmin.php?action=votepoll&fid=$fid&tid=$tid\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td class=\\\"header\\\" colspan=\\\"2\\\">投票标题：$thread[subject]</td></tr>\n$pollhtml\n</table>\n</td></tr></table>\n\n$buttoncode\n</form>');
INSERT INTO cdb_templates VALUES('36','viewthread_post','0','<a name=\\\"pid$post[pid]\\\">\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\" style=\\\"table-layout: fixed; word-wrap: break-word\\\">\n<tr bgcolor=\\\"$thisbg\\\">\n<td valign=\\\"top\\\" width=\\\"21%\\\">$ip<span class=\\\"bold\\\"\\\">$post[author]</span><br>\n$showtitle<br>$stars<br>\n$avatar<br>\n$credittitle $post[credit] $creditunit<br>\n发帖 $post[postnum] 篇<br>\n注册 $tharegdate\n$location<br>\n$personstatus</td>\n<td height=\\\"100%\\\">\n<table height=\\\"100%\\\" width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" style=\\\"table-layout: fixed;word-wrap: break-word\\\">\n<tr><td valign=\\\"top\\\">\n<font class=\\\"subject\\\"><span class=\\\"bold\\\">$post[subject]</span></font>$post[message]\n</td></tr></table>\n</td></tr>\n<tr bgcolor=\\\"$thisbg\\\"><td valign=\\\"middle\\\" class=\\\"smalltxt\\\">$delete $post[icon] $poston</td><td valign=\\\"middle\\\">\n<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\">\n<tr class=\\\"smalltxt\\\"><td align=\\\"left\\\">\n$profile$email$site$search$u2u$oicq$icq$yahoo&nbsp;\n</td><td align=\\\"right\\\">\n$edit$repquote$reportlink$karma</td></tr></table>\n</td></tr>\n</table>\n</td></tr></table></a>');
INSERT INTO cdb_templates VALUES('37','viewthread','0','$poll\n<form name=\\\"delpost\\\" method=\\\"post\\\" action=\\\"topicadmin.php?action=delpost&fid=$fid&tid=$tid&page=$page\\\">\n<table width=\\\"$tablewidth\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" align=\\\"center\\\">\n<tr><td class=\\\"multi\\\">$multipage$creditsrequire</td><td align=\\\"right\\\">\n<a href=\\\"post.php?action=newthread&fid=$fid\\\"><img src=\\\"$imgdir/newtopic.gif\\\" border=\\\"0\\\"></a>$newpolllink$replylink\n</td></tr></table>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><td width=\\\"21%\\\">作者：</td>\n\n<td><table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"100%\\\" style=\\\"table-layout: fixed; word-wrap: break-word\\\">\n<tr style=\\\"color: $headertext\\\"><td width=\\\"80%\\\" class=\\\"bold\\\" nowrap>主题：$thread[subject]</td>\n<td align=\\\"right\\\" nowrap>$nohllink<a href=\\\"viewthread.php?fid=$fid&tid=$tid&goto=nextoldset\\\" style=\\\"color: $headertext;font-weight: normal\\\">上一主题</a> \n<a href=\\\"viewthread.php?fid=$fid&tid=$tid&goto=nextnewset\\\" style=\\\"color: $headertext;font-weight: normal\\\">下一主题</a></td>\n</tr></table></td>\n\n</tr></table></td></tr></table>\n\n$posts\n\n<table width=\\\"$tablewidth\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" align=\\\"center\\\">\n<tr>\n<td class=\\\"multi\\\" valign=\\\"top\\\">$multipage</td><td align=\\\"right\\\"><br>\n<a href=\\\"post.php?action=newthread&fid=$fid\\\"><img src=\\\"$imgdir/newtopic.gif\\\" border=\\\"0\\\"></a>$newpolllink$replylink\n</td></tr></table></form>\n\n<br><table width=\\\"310\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" align=\\\"center\\\">\n<td align=\\\"center\\\" bgcolor=\\\"$bordercolor\\\" width=\\\"30%\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" width=\\\"100%\\\">\n<tr><td bgcolor=\\\"$altbg2\\\" class=\\\"mediumtxt\\\" align=\\\"center\\\">\n<a href=\\\"viewthread.php?fid=$fid&tid=$tid&action=printable\\\" class=\\\"mediumtxt\\\">可打印版本</a> | \n<a href=\\\"topicadmin.php?action=emailfriend&tid=$tid\\\" class=\\\"mediumtxt\\\">推荐给朋友</a> | \n<a href=\\\"memcp.php?action=subscriptions&subadd=$tid\\\" class=\\\"mediumtxt\\\">订阅主题</a> | \n<a href=\\\"memcp.php?action=favorites&favadd=$tid\\\" class=\\\"mediumtxt\\\">收藏主题</a>\n</td></tr></table></td></tr></table><br>\n$fastpost_viewthread<br>\n<table width=\\\"$tablewidth\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" align=\\\"center\\\">\n<tr><td align=\\\"left\\\">$forumjump</td><td align=\\\"right\\\">$modoptions</td>\n</tr></table><br><br>');
INSERT INTO cdb_templates VALUES('38','viewthread_fastpost','0','<script language=\\\"JavaScript\\\">\nvar postmaxchars = $maxpostsize;\nvar isadmin = $isadmin;\nfunction validate(theform) {\n        if (theform.message.value == \\\"\\\" && theform.subject.value == \\\"\\\") {\n                alert(\\\"请完成标题或内容栏。\\\");\n                return false; }\n        if (postmaxchars != 0 && isadmin != 1) {\n                if (theform.message.value.length > $maxpostsize) {\n                        alert(\\\"您的贴子长度超过限制\\\\n\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n系统允许：$maxpostsize 字符\\\");\n                        return false; }\n                else { return true; }\n        } else { return true; }\n}\n</script>\n<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"post.php?action=reply&fid=$fid&tid=$tid\\\" $enctype onSubmit=\\\"return validate(this)\\\">\n<input type=\\\"hidden\\\" name=\\\"replysubmit\\\" value=\\\"submit\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\" bgcolor=\\\"$bordercolor\\\"><tr><td>\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td colspan=\\\"2\\\" class=\\\"header\\\">快速回复主题</td></tr>\n<tr>\n<td width=\\\"18%\\\" bgcolor=\\\"$altbg1\\\">主题：</td>\n<td width=\\\"82%\\\" bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"80\\\" value=\\\"\\\" tabindex=\\\"1\\\">&nbsp;&nbsp;(可选)</td>\n</tr>\n<tr>\n<td width=\\\"18%\\\" bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">选项：<br><br>\n<input type=\\\"checkbox\\\" name=\\\"parseurloff\\\" value=\\\"1\\\"> 禁用 URL 识别<br>\n<input type=\\\"checkbox\\\" name=\\\"smileyoff\\\" value=\\\"1\\\" $smileyoffcheck> 禁用 Smilies<br>\n<input type=\\\"checkbox\\\" name=\\\"bbcodeoff\\\" value=\\\"1\\\" $codeoffcheck> 禁用 Discuz! 代码<br>\n<input type=\\\"checkbox\\\" name=\\\"usesig\\\" value=\\\"1\\\" $usesigcheck> 使用个人签名<br>\n</td>\n<td width=\\\"82%\\\" bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"7\\\" cols=\\\"90\\\" name=\\\"message\\\" onKeyDown=\\\"javascript: ctlent();\\\" tabindex=\\\"2\\\"></textarea><br>\n<input type=\\\"submit\\\" name=\\\"replysubmit\\\" value=\\\"发表回复\\\" tabindex=\\\"3\\\">&nbsp;&nbsp;&nbsp;\n<input type=\\\"submit\\\" name=\\\"previewpost\\\" value=\\\"预览贴子\\\" tabindex=\\\"4\\\">&nbsp;&nbsp;&nbsp;\n<input type=\\\"reset\\\" name=\\\"topicsreset\\\" value=\\\"清空内容\\\" tabindex=\\\"5\\\">&nbsp;&nbsp;&nbsp;[完成后可按 Ctrl+Enter 发布]</td>\n</tr></table></td></tr></table></form>');
INSERT INTO cdb_templates VALUES('39','faq_messages_smilierow','0','<tr>\n<td width=\\\"25%\\\" bgcolor=\\\"$altbg2\\\" align=\\\"center\\\">$smilie[code]</td>\n<td width=\\\"75%\\\" bgcolor=\\\"$altbg2\\\" align=\\\"center\\\"><img src=\\\"$smdir/$smilie[url]\\\"></td>\n</tr>');
INSERT INTO cdb_templates VALUES('40','faq_misc','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr>\n<td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">其他问题</td>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"#1\\\">能说明一下 Discuz! 代码的用法吗？</a></li>\n<li><a href=\\\"#2\\\">普通用户如何成为版主？</a></li>\n<li><a href=\\\"#3\\\">我在 $bbname 具有哪些权限？</a></li>\n</td>\n</tr>\n</table>\n</table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr>\n<td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">能说明一下 Discuz! 代码的用法吗？</td>\n<a name=#1></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;您可以使用 Discuz! 代码--一个 HTML 代码的简化版本，来简化对贴子显示格式的控制。<br><br>\n<ol type=\\\"1\\\">\n<li>[b]粗体文字 Abc[/b] &nbsp; 效果：<b>粗体文字 Abc</b> （粗体字）<br><br></li>\n<li>[i]斜体文字 Abc[/i] &nbsp; 效果：<i>斜体文字 Abc</i> （斜体字）<br><br></li>\n<li>[u]下划线文字 Abc[/u] &nbsp; 效果：<u>下划线文字 Abc</u> （下划线）<br><br></li>\n<li>[fly]飞行文字 Abc[/fly] &nbsp; （飞行文字） &nbsp; 效果：<marquee width=\\\"90%\\\" behavior=\\\"alternate\\\" scrollamount=\\\"3\\\">飞行文字 Abc</marquee><br><br></li>\n<li>[color=red]红颜色[/color] &nbsp; 效果：<font color=\\\"red\\\">红颜色</font> （改变文字颜色）<br><br></li>\n<li>[size=3]文字大小为 3[/size] &nbsp; 效果：<font size=\\\"3\\\">文字大小为 3</font> （改变文字大小）<br><br></li>\n<li>[font=仿宋]字体为仿宋[/font] &nbsp; 效果：<font face\\\"仿宋\\\">字体为仿宋</font> （改变字体）<br><br></li>\n<li>[align=Center]内容居中[/align] &nbsp; （格式内容位置） 效果：<br><center>内容居中</center><br></li>\n<li>[url]http://www.crossday.com[/url] &nbsp; 效果：<a href=\\\"http://www.crossday.com\\\" target=\\\"_blank\\\">http://www.crossday.com</a> （超级连接）<br><br></li>\n<li>[url=http://www.Discuz.net]Discuz! 论坛[/url] &nbsp; 效果：<a href=\\\"http://www.Discuz.net\\\" target=\\\"_blank\\\">Discuz! 论坛</a> （超级连接）<br><br></li>\n<li>[email]support@crossday.com[/email] &nbsp; 效果：<a href=\\\"mailto:support@crossday.com\\\">support@crossday.com</a> （E-Mail 链接）<br><br></li>\n<li>[email=support@discuz.net]Discuz! 技术支持[/email] &nbsp; 效果：<a href=\\\"mailto:support@discuz.net\\\">Discuz! 技术支持</a> （E-Mail 链接）<br><br></li>\n<li>[quote]Discuz! Board 版权所有 - Crossday Studio[/quote] &nbsp; （引用内容，类似的代码还有 [code][/code]）<br><br></li>\n<li>[hide]免费账号为：163/163[/hide] &nbsp; （按回复隐藏内容，仅限版主及管理员使用）<br>效果：只有当浏览者回复本贴时，才显示其中的内容，否则显示为“<b>**** 隐藏信息 跟贴后才能显示 *****</b>”<br><br></li>\n<li>[hide=20]免费账号为：163/163[/hide] &nbsp; （按{$credittitle}隐藏内容，仅限版主及管理员使用）<br>效果：只有当浏览者{$credittitle}高于 20 {$creditunit}时，才显示其中的内容，否则显示为“<b>**** 隐藏信息 {$credittitle}高于 20 点才能显示 ****</b>”<br><br></li>\n<li>[list]<br>\n[*]列表项 #1<br>\n[*]列表项 #2<br>\n[*]列表项 #2<br>\n[/list] &nbsp; (列表)<br><br></li>\n<br>以下 Discuz! 代码需论坛允许 [img] 代码才能使用<hr noshade size=\\\"0\\\" width=\\\"50%\\\" color=\\\"$bordercolor\\\" align=\\\"left\\\"><br>\n<li>[iframe]http://www.crossday.com[/iframe] &nbsp; （在贴子中镶嵌网页）<br><br></li>\n<li>[img]http://www.crossday.com/statdard/images/logo.gif[/img] &nbsp; （链接图像）<br>效果：<br><img src=\\\"images/standard/logo.gif\\\"> <br><br></li>\n<li>[img=88,31]http://www.crossday.com/images/logo.gif[/img] &nbsp;（链接图像并限制大小）<br> 效果：<br><img src=\\\"images/logo.gif\\\" height=\\\"31\\\" width=\\\"88\\\"> <br><br></li>\n<li>[swf]http://www.crossday.com/cdb/images/banner.swf[/swf] &nbsp; （链接 flash 动画，用法与 [img] 类似）<br><br></li>\n<li>[swf=88,31]http://www.crossday.com/cdb/images/banner.swf[/swf] &nbsp;（链接 flash 动画并限制大小，用法与 [img] 类似）<br><br></li>\n<li>[wmv][/wmv]、[mid][/mid]、[ra][/ra]、[rm][/rm] &nbsp; （用于在贴子中插入上述多媒体格式文件，用法与其他 Discuz! 代码类似）<br><br></li>\n</ol>\n</td></tr></table>\n</td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr>\n<td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">普通用户如何成为版主？</td>\n<a name=#2></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;论坛的版主是自愿申请的，管理员可能会要求版主需要达到一定{$credittitle}，或在论坛注册超过一定时间等。\n版主应该是诚实守信、乐于助人、大公无私的表率，同时还要熟悉专业，经验丰富，有良好的口碑。如果你确认已经达到上面几点，并希望担任本站\n的版主，可以与管理员联系。\n</td></tr></table>\n</td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr>\n<td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">我在 $bbname 具有哪些权限？</td>\n<a name=#3></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;本站会员等级是按照{$credittitle}区分的，Discuz! 可以依照您的发帖量，其他用户的评分，或两者综合来决定\n{$credittitle}。当{$credittitle}达到一定等级要求时，系统会自动给您升级并给予相应的权限和星星标志。下面是您{$current}的级别\n和权限详情：<br><br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"600\\\" align=center><tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n$groupinfo\n</table>\n</td></tr></table><br><br>\n<form method=\\\"post\\\" action=\\\"faq.php?page=misc#3\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"600\\\" align=center><tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">查询用户组：</td>\n<td bgcolor=\\\"$altbg2\\\" align=\\\"center\\\">$groupselect</td>\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">查询权限：</td>\n<td bgcolor=\\\"$altbg2\\\" align=\\\"center\\\">$permselect</td>\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\"><input type=\\\"submit\\\" name=\\\"searchsubmit\\\" value=\\\"查 询\\\"></td>\n</tr></table></td></tr></table></form>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"450\\\" align=center>\n<tr><td>$grouplist</td></tr></table>\n</td></tr></table>\n</td></tr></table>');
INSERT INTO cdb_templates VALUES('41','memcp_navbar','0','<table cellpadding=\\\"0\\\" cellspacing=\\\"0\\\" border=\\\"0\\\" bgcolor=\\\"$bordercolor\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td>\n<table cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" border=\\\"0\\\" width=\\\"100%\\\">\n<tr align=\\\"center\\\">\n<td bgcolor=\\\"$navcolor[home]\\\">$navlink[home]</td>\n<td bgcolor=\\\"$navcolor[profile]\\\">$navlink[profile]</td>\n<td bgcolor=\\\"$navcolor[subscriptions]\\\">$navlink[subscriptions]</td>\n<td bgcolor=\\\"$navcolor[favorites]\\\">$navlink[favorites]</td>\n<td bgcolor=\\\"$altbg2\\\"><a href=\\\"###\\\" onclick=\\\"Popup(\\\'u2u.php?sid=$sid\\\', \\\'u2u\\\', 580, 450);\\\">短 消 息</a></td>\n<td bgcolor=\\\"$altbg2\\\"><a href=\\\"###\\\" onclick=\\\"Popup(\\\'buddy.php?sid=$sid\\\', \\\'buddylist\\\', 250, 300);\\\">好友列表</a></td>\n</tr></table></td></tr></table><br>');
INSERT INTO cdb_templates VALUES('42','buddylist_buddy_online','0','<tr><td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\" onMouseOver =\\\"this.style.backgroundColor=\\\'$altbg2\\\'\\\" onMouseOut =\\\"this.style.backgroundColor=\\\'$altbg1\\\'\\\"><a href=\\\"member.php?action=viewpro&username=$encodename\\\" target=\\\"_blank\\\">$buddy[buddyname]</a></td></tr>');
INSERT INTO cdb_templates VALUES('43','faq','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">用户须知</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"faq.php?page=usermaint#1\\\">我必须要注册吗？</a></li>\n<li><a href=\\\"faq.php?page=usermaint#2\\\">Discuz! 论坛使用 Cookies 吗？</a></li>\n<li><a href=\\\"faq.php?page=usermaint#3\\\">如何使用签名？</a></li>\n<li><a href=\\\"faq.php?page=usermaint#4\\\">如何使用个性化的头像？</a></li>\n<li><a href=\\\"faq.php?page=usermaint#5\\\">如果我遗忘了密码，我该怎么办？</a></li>\n<li><a href=\\\"faq.php?page=usermaint#6\\\">什么是“短消息”？</a></li>\n</td>\n</tr>\n</table></td></tr></table>\n\n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">论坛使用</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"faq.php?page=using#1\\\">在哪里可以登录？</a></li>\n<li><a href=\\\"faq.php?page=using#2\\\">在哪里可以退出？</a></li>\n<li><a href=\\\"faq.php?page=using#3\\\">我要搜索论坛，应该怎么做</a></li>\n<li><a href=\\\"faq.php?page=using#4\\\">怎样给其他人发送“短消息”？</a></li>\n<li><a href=\\\"faq.php?page=using#5\\\">怎样看到全部的会员？</a></li>\n</td>              \n</tr>\n</table></td></tr></table>\n\n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">读写贴子和收发短消息</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"faq.php?page=messages#1\\\">如何发布新贴子？</a></li>\n<li><a href=\\\"faq.php?page=messages#2\\\">如何回复贴子？</a></li>\n<li><a href=\\\"faq.php?page=messages#3\\\">我能够删除主题吗？</a></li>\n<li><a href=\\\"faq.php?page=messages#4\\\">怎样编辑自己发表的贴子？</a></li>\n<li><a href=\\\"faq.php?page=messages#5\\\">我可不可以上传附件？</a></li>\n<li><a href=\\\"faq.php?page=messages#6\\\">什么是 Smilies？</a></li>\n<li><a href=\\\"faq.php?page=messages#7\\\">该怎样发起一个投票？</a></li>\n</td>\n</tr>\n</table></td></tr></table>\n\n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">其他问题</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"faq.php?page=misc#1\\\">能说明一下 Discuz! 代码的用法吗？</a></li>\n<li><a href=\\\"faq.php?page=misc#2\\\">普通用户如何成为版主？</a></li>\n<li><a href=\\\"faq.php?page=misc#3\\\">我在 $bbname 具有哪些权限？</a></li>\n</td>\n</tr>\n</table></td></tr></table>');
INSERT INTO cdb_templates VALUES('44','faq_using','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">论坛使用</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"#1\\\">在哪里可以登录？</a></li>\n<li><a href=\\\"#2\\\">在哪里可以退出？</a></li>\n<li><a href=\\\"#3\\\">我要搜索论坛，应该怎么做？</a></li>\n<li><a href=\\\"#4\\\">怎样给其他人发送“短消息”？</a></li>\n<li><a href=\\\"#5\\\">怎样看到全部的会员？</a></li>\n</td>\n</tr>\n</table></td></tr></table>\n\n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">在哪里可以登录？</td> <a name=#1></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;如果您尚未登录，点击左上角的“登录”，输入用户名和密码，确定即可。\n</td>\n</tr>\n</table></td></tr></table>\n        \n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">在哪里可以退出？</td><a name=#2></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;如果您已经登录，点击左上角的“退出”，系统会清除 Cookies，退出登录状态。\n</td>\n</tr>\n</table></td></tr></table>\n        \n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">我要搜索论坛，应该怎么做？</td><a name=#3></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;点击上面的 <a href=\\\"misc.php?action=search\\\">论坛搜索</a>，输入想搜索的文字内容并选择一个范围，就可以检索到相关的贴子。\n</td>\n</tr>\n</table></td></tr></table>\n        \n<br>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">怎样给其他人发送“短消息”？</td><a name=#4></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;如果您已登录，菜单上会显示出 <a href=\\\"###\\\" onclick=\\\"Popup(\\\'u2u.php?sid=$sid\\\', \\\'u2u\\\', 580, 450);\\\">短消息</a> 项，点击后弹出短消息窗口，通过类似发送邮件一样的填写，点“发送”，消息就被发到对方收件箱中了。当他/她再次登录论坛时，系统将提示他/她收信息。</td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">怎样看到全部的会员？</td><a name=#5></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;您可以通过点击 <a href=\\\"member.php?action=list\\\">会员</a> 查看所有的会员及其资料，并可实现会员资料的检索和排序输出。</td>\n</tr>\n</table></td></tr></table>');
INSERT INTO cdb_templates VALUES('45','faq_messages','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">读写贴子和收发短消息</td> \n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n<ul>\n<li><a href=\\\"#1\\\">如何发布新贴子？</a></li>\n<li><a href=\\\"#2\\\">如何回复贴子？</a></li>\n<li><a href=\\\"#3\\\">我能够删除主题吗？</a></li>\n<li><a href=\\\"#4\\\">怎样编辑自己发表的贴子？</a></li>\n<li><a href=\\\"#5\\\">我可不可以上传附件？</a></li>\n<li><a href=\\\"#6\\\">什么是 Smilies？</a></li>\n<li><a href=\\\"#7\\\">该怎样发起一个投票？</a></li>\n</td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">如何发布新贴子？</td><a name=#1></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;在论坛板块中，点“发新贴”即可进入功能齐全的发帖界面。当然您也可以使用板块下面的“快速发帖”发表新帖(如果此选项打开)。注意，一般论坛都设置为需要登录后才能发帖。\n</td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">如何回复贴子？</td><a name=#2></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;在浏览贴子时，点“回复贴子”即可进入功能齐全的回复界面。当然您也可以使用板块下面的“快速回复”发表回复(如果此选项打开)。注意，一般论坛都设置为需要登录后才能回复。\n</td>\n</tr>\n</table></td></tr></table>        \n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">我能够删除主题吗？</td><a name=#3></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;浏览贴子时可以按下面的“编辑贴子”，对于您自己发表的贴子，可以很容易的编辑和删除。但当这贴是整个主题的起始贴时，则会删除该主题和全部回复。\n</td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">怎样编辑自己发表的贴子？</td><a name=#4></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;和上面一样，用“编辑贴子”就可以编辑自己发表的贴子。如果管理员通过论坛设置将这个功能屏蔽掉则不再可以进行此操作。\n</td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">我可不可以上传附件？</td> <a name=#5></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;可以。您可以在任何允许上传附件的板块中，通过发新贴、或者回复的方式上传附件（只要您的权限足够）。附件必须在系统限定尺寸以下才能上传。\n</td>\n</tr>\n</table></td></tr></table>        \n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">什么是 Smilies？</td><a name=#6></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;Smilies 是一些用字符表示的表情符号，如果打开 Smilies 功能，Discuz! 会把一些符号转换成小图像，显示在贴子中，更加美观明了。目前支持下面这些 Smilies：<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"50%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"25%\\\" class=\\\"header\\\" align=\\\"center\\\">表情符号</td>\n<td width=\\\"75%\\\" class=\\\"header\\\" align=\\\"center\\\">对应图像</td>\n</tr>\n$smilierows\n</table></td></tr></table></td>\n</tr>\n</table></td></tr></table>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\"> \n<tr><td bgcolor=\\\"$bordercolor\\\"> \n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\"> \n<tr>\n<td width=\\\"100%\\\" class=\\\"header\\\">该怎样发起一个投票？</td><a name=#7></a>\n</tr>\n<tr>\n<td width=\\\"100%\\\" bgcolor=\\\"$altbg2\\\">\n&nbsp;&nbsp;&nbsp;&nbsp;您可以像发帖一样在板块中发起投票。每行输入一个可能的选项（最多10个），您可以通过阅读这个投票贴选出自己的答案，每人只能投票一次，之后将不能再对您的选择做出更改。<br><br>\n&nbsp;&nbsp;&nbsp;&nbsp;管理员拥有随时取消投票的权力。\n</td>\n</tr>\n</table></td></tr></table>');
INSERT INTO cdb_templates VALUES('46','buddylist_buddy_offline','0','<tr><td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\" onMouseOver =\\\"this.style.backgroundColor=\\\'$altbg2\\\'\\\" onMouseOut =\\\"this.style.backgroundColor=\\\'$altbg1\\\'\\\"><a href=\\\"member.php?action=viewpro&username=$encodename\\\" target=\\\"_blank\\\">$buddy[buddyname]</a></td></tr>');
INSERT INTO cdb_templates VALUES('47','buddylist','1','<html>\r\n<head>\r\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=$charset\\\">\r\n<meta name=\\\"generator\\\" content=\\\"Discuz! Board $version\\\">\r\n\r\n$css\r\n<title>$bbname - Powered by Discuz! Board</title>\r\n</head>\r\n<body>\r\n\r\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"96%\\\" align=\\\"center\\\">\r\n<tr><td bgcolor=\\\"$bordercolor\\\">\r\n\r\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\r\n<tr>\r\n<td class=\\\"header\\\">好友列表</td>\r\n</tr>\r\n<tr><td bgcolor=\\\"$altbg1\\\">\r\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"98%\\\">\r\n<tr>\r\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"2\\\"><span class=\\\"bold\\\"><center>Online</center></span></td>\r\n</tr>\r\n$buddys[online]\r\n<tr>\r\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"2\\\"><span class=\\\"bold\\\"><center>Offline</center></span></td>\r\n</tr>\r\n$buddys[offline]\r\n</table>\r\n<tr>\r\n<td bgcolor=\\\"$altbg2\\\"><span class=\\\"bold\\\"><a href=\\\"buddy.php\\\"><center>刷新好友列表</center></a></span></td>\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n<br>\r\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"96%\\\" align=\\\"center\\\">\r\n<tr><td bgcolor=\\\"$bordercolor\\\">\r\n\r\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\r\n<tr>\r\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">\r\n<a href=\\\"buddy.php\\\">好友列表</a><br>\r\n<a href=\\\"buddy.php?action=edit\\\">编辑好友列表</a>\r\n</td>\r\n</tr></table>\r\n</td></tr></table>\r\n\r\n</body>\r\n</html>');
INSERT INTO cdb_templates VALUES('48','buddylist_edit','0','<html>\n<head>\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=$charset\\\">\n<meta name=\\\"generator\\\" content=\\\"Discuz! Board $version\\\">\n\n$css\n<title>$bbname - Powered by Discuz! Board</title>\n</head>\n<body>\n\n<form method=\\\"post\\\" action=\\\"buddy.php?action=edit\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"96%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td class=\\\"header\\\">编辑好友列表</td>\n</tr>\n<tr><td bgcolor=\\\"$altbg1\\\">\n<table width=\\\"98%\\\">\n<tr><center>\n$buddys\n<tr><td bgcolor=\\\"$altbg1\\\" colspan=\\\"2\\\" align=\\\"center\\\"><input type=\\\"text\\\" name=\\\"newbuddy1\\\" value=\\\"\\\"></td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" colspan=\\\"2\\\" align=\\\"center\\\"><input type=\\\"text\\\" name=\\\"newbuddy2\\\" value=\\\"\\\"></td></tr>\n</table>\n<tr>\n<td bgcolor=\\\"$altbg2\\\"><center><input type=\\\"submit\\\" name=\\\"editsubmit\\\" value=\\\"更 新\\\"></td>\n</tr>\n</table>\n</td></tr></table></form>\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"96%\\\" align=\\\"center\\\">\n<tr> <td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td bgcolor=\\\"$altbg1\\\">\n<font class=\\\"12px\\\">\n<div align=\\\"center\\\">\n<a href=\\\"buddy.php\\\">好友列表</a><br><a href=\\\"buddy.php?action=edit\\\">编辑好友列表</a>\n</font></div></td>\n</tr></table>\n</td></tr></table>\n\n</body>\n</html>');
