INSERT INTO cdb_templates VALUES('51','buddylist_message','0','<html>\n<head>\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=$charset\\\">\n<meta name=\\\"generator\\\" content=\\\"Discuz! Board $version\\\">\n$url_redirect\n$css\n<title>$bbname - Powered by Discuz! Board</title>\n</head>\n<body>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"96%\\\" height=\\\"250\\\" align=\\\"center\\\">\n<tr><td valign=\\\"middle\\\">\n\n<table border=\\\"0\\\" bgcolor=\\\"$bordercolor\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">\n<span class=\\\"smalltxt\\\">\n$message\n$redirectjs\n</span></td>\n</tr></table>\n</td></tr></table>\n</body></html>');
INSERT INTO cdb_templates VALUES('52','u2u','0','$u2uheader\n<form method=\\\"post\\\" action=\\\"u2u.php?action=delete\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"98%\\\" align=\\\"center\\\">\n<tr><td class=\\\"multi\\\">$multipage</td></tr>\n\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td class=\\\"header\\\" colspan=\\\"4\\\" align=\\\"center\\\">$inboxoutbox</td></tr>\n<tr class=\\\"header\\\" align=\\\"center\\\">\n<td width=\\\"45\\\"><input type=\\\"checkbox\\\" name=\\\"chkall\\\" class=\\\"header\\\" onclick=\\\"checkall(this.form)\\\">删?</td>\n<td width=\\\"50%\\\">主题</td>\n<td>$tofrom</td>\n<td>发送日期</td>\n</tr>\n$messages\n</table></td></tr>\n<tr><td class=\\\"multi\\\">$multipage</td></tr>\n</table><br>\n<input type=\\\"hidden\\\" name=\\\"folder\\\" value=\\\"$folder\\\">\n<input type=\\\"submit\\\" name=\\\"alldel\\\" value=\\\"更 新\\\">\n</form>\n$u2ufooter');
INSERT INTO cdb_templates VALUES('53','u2u_header','0','<html>\n<head>\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=$charset\\\">\n<meta name=\\\"generator\\\" content=\\\"Discuz! Board $version\\\">\n$url_redirect\n$css\n<script language=\\\"JavaScript\\\">\nfunction ctlent(obj) {if((event.ctrlKey && window.event.keyCode == 13) || (event.altKey && window.event.keyCode == 83)) {this.document.input.submit();}}\nfunction checkall(form) {for(var i = 0;i < form.elements.length; i++) {var e = form.elements[i];if (e.name != \\\'chkall\\\') e.checked = form.chkall.checked;}}\nfunction findobj(n, d) {var p,i,x; if(!d) d=document; if((p=n.indexOf(\\\"?\\\"))>0&&parent.frames.length) {d=parent.frames[n.substring(p+1)].document; n=n.substring(0,p);} if(!(x=d[n])&&d.all) x=d.all[n]; for(i=0;!x&&i<d.forms.length;i++) x=d.forms[i][n]; for(i=0;!x&&d.layers&&i>d.layers.length;i++) x=MM_findObj(n,d.layers[i].document); return x;}\nfunction copycode(obj) {var rng = document.body.createTextRange();rng.moveToElementText(obj);rng.scrollIntoView();rng.select();rng.execCommand(\\\"Copy\\\");rng.collapse(false);}\n</script>\n\n<title>$bbname - Powered by Discuz! Board</title>\n</head>\n<body>\n\n<font class=\\\"mediumtxt\\\">\n<div align=\\\"center\\\" class=\\\"mediumtxt\\\">\n<a href=\\\"u2u.php\\\" class=\\\"mediumtxt\\\">收件箱</a> -\n<a href=\\\"u2u.php?folder=outbox\\\" class=\\\"mediumtxt\\\">发件箱</a> -\n<a href=\\\"u2u.php?action=send\\\" class=\\\"mediumtxt\\\">发送短消息</a> -\n<a href=\\\"u2u.php?action=ignore\\\" class=\\\"mediumtxt\\\">忽略列表</a>\n</font><br><br>');
INSERT INTO cdb_templates VALUES('54','u2u_footer','0','<br><br><font class=\\\"mediumtxt\\\">\n<a href=\\\"u2u.php\\\" class=\\\"mediumtxt\\\">收件箱</a> -\n<a href=\\\"u2u.php?folder=outbox\\\" class=\\\"mediumtxt\\\">发件箱</a> -\n<a href=\\\"u2u.php?action=send\\\" class=\\\"mediumtxt\\\">发送短消息</a> -\n<a href=\\\"u2u.php?action=ignore\\\" class=\\\"mediumtxt\\\">忽略列表</a>\n</body>\n</html>');
INSERT INTO cdb_templates VALUES('55','u2u_send_buddylist','0','<tr><td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\" id=\\\"buddy\\\">好友群发：<br>\n全部好友<input type=\\\"checkbox\\\" name=\\\"chkall\\\" onclick=\\\"for(var i = 0;i < form.elements.length; i++) {var e = form.elements[i];\nif (e.name != \\\'chkall\\\' && e.name != \\\'saveoutbox\\\') e.checked = form.chkall.checked;}\\\"></td>\n<td bgcolor=\\\"$altbg2\\\">\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" width=\\\"100%\\\">$msgtob</table>\n</td></tr>');
INSERT INTO cdb_templates VALUES('56','u2u_send','0','$u2uheader\n<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"u2u.php?action=send\\\">\n<input type=\\\"hidden\\\" name=\\\"u2usubmit\\\" value=\\\"submit\\\">\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"98%\\\" align=\\\"center\\\">\n<tr> <td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td width=\\\"100%\\\" colspan=\\\"2\\\" class=\\\"header\\\">发送短消息</td>\n</tr>\n\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"18%\\\">收件人：</td>\n<td bgcolor=\\\"$altbg2\\\" width=\\\"82%\\\"><input type=\\\"text\\\" name=\\\"msgtoa\\\" size=\\\"50\\\" value=\\\"$touser\\\"></td></tr>\n\n$buddylist\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">主题：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"65\\\" value=\\\"$subject\\\"></td>\n</tr>\n\n<tr>\n<td valign=\\\"top\\\" bgcolor=\\\"$altbg1\\\">消息内容：</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"8\\\" name=\\\"message\\\" cols=\\\"65\\\" onKeyDown=\\\"javascript: ctlent();\\\">$message</textarea><BR>\n<input type=\\\"checkbox\\\" name=\\\"saveoutbox\\\" value=\\\"1\\\">保存到发件箱中 &nbsp; [完成后可按 Ctrl+Enter 发布]</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<input type=\\\"submit\\\" name=\\\"u2usubmit\\\" value=\\\"发送短消息\\\">\n</form>\n$u2ufooter');
INSERT INTO cdb_templates VALUES('57','u2u_ignore','0','$u2uheader\n<form method=\\\"post\\\" action=\\\"u2u.php?action=ignoresubmit\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"98%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td class=\\\"header\\\" colspan=\\\"2\\\">忽略列表</td></tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">忽略列表：<br>添加多个忽略人员名单时用逗号 \\\",\\\" 隔开(如：张三,李四,王五等)。当这些被添加的忽略用户给您发送短消息时将不予接收。</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"5\\\" cols=\\\"30\\\" name=\\\"ignorelist\\\">$mem[ignoreu2u]</textarea></td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<input type=\\\"submit\\\" value=\\\"更 新\\\">\n</form>\n$u2ufooter');
INSERT INTO cdb_templates VALUES('58','u2u_view_refwdlinks','0','<tr>\n<td bgcolor=\\\"$altbg1\\\" colspan=\\\"2\\\"><a href=\\\"u2u.php?action=send&u2uid=$u2uid&do=reply\\\">回复</a> - <a href=\\\"u2u.php?action=send&u2uid=$u2uid&do=forward\\\">转发</a></td>\n</tr>');
INSERT INTO cdb_templates VALUES('59','u2u_message','0','$u2uheader\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"98%\\\" align=\\\"center\\\">\n<tr> <td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td bgcolor=\\\"$altbg1\\\">\n<font class=\\\"12px\\\">\n<div align=\\\"center\\\">\n$message\n$redirectjs\n</font></div></td>\n</tr></table>\n</td></tr></table>\n$u2ufooter');
INSERT INTO cdb_templates VALUES('60','u2u_view','0','$u2uheader\n<form method=\\\"post\\\" action=\\\"u2u.php?action=delete\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"98%\\\" align=\\\"center\\\">\n<tr>\n<td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td  class=\\\"header\\\" colspan=\\\"2\\\">$u2u[subject]</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">发信人：</td>\n<td bgcolor=\\\"$altbg2\\\">$u2u[msgfrom]</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">收信人：</td>\n<td bgcolor=\\\"$altbg2\\\">$u2u[msgto]</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">发送日期：</td>\n<td bgcolor=\\\"$altbg2\\\">$dateline</td>\n</tr>\n\n<tr>\n<td valign=\\\"top\\\" bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">消息内容：</td>\n<td bgcolor=\\\"$altbg2\\\">$u2u[message]</td>\n</tr>\n$refwdlinks\n</table>\n</center></div></td>\n</tr>\n</table><br>\n<input type=\\\"hidden\\\" name=\\\"u2uid\\\" value=\\\"$u2uid\\\">\n<input type=\\\"submit\\\" name=\\\"imsubmit\\\" value=\\\"删 除\\\"></form>\n$u2ufooter');
INSERT INTO cdb_templates VALUES('61','misc_search','0','<form method=\\\"post\\\" action=\\\"misc.php?action=search\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"4\\\">论坛搜索</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\" valign=\\\"top\\\">关 键 字：</td>\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"3\\\"><input type=\\\"text\\\" name=\\\"srchtxt\\\" size=\\\"25\\\" maxlength=\\\"40\\\">\n&nbsp;&nbsp;&nbsp;关键字中可使用通配符 \\\"*\\\" 匹配，如 \\\"想去*和*约会\\\"\n<br><br>同时匹配多个关键字，请用空格 \\\" \\\"、加号 \\\"+\\\"、\\\"&\\\" 或 \\\"AND\\\" 连接。如 windows AND unix\n<br>匹配多个关键字其中部分，请用 \\\"|\\\" 或 \\\"OR\\\" 连接。如 windows OR unix\n</td>\n</tr>        \n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">搜索用户：</td>\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"3\\\"><input type=\\\"text\\\" name=\\\"srchuname\\\" size=\\\"25\\\" maxlength=\\\"40\\\">\n&nbsp;&nbsp;&nbsp;如需部分匹配用户名，请使用 \\\"*\\\"，如 *user*name*\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">搜索论坛：</td>\n<td bgcolor=\\\"$altbg2\\\" width=\\\"35%\\\">$forumselect</td>\n\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">时间范围：</td>\n<td bgcolor=\\\"$altbg2\\\" width=\\\"35%\\\"><select name=\\\"srchfrom\\\">\n<option value=\\\"0\\\" selected=\\\"selected\\\">全部时间</option>\n<option value=\\\"86400\\\">1 天以来</option>\n<option value=\\\"172800\\\">2 天以来</option>\n<option value=\\\"432000\\\">1 周以来</option>\n<option value=\\\"1296000\\\">1 个月以来</option>\n<option value=\\\"5184000\\\">3 个月以来</option>\n<option value=\\\"8640000\\\">6 个月以来</option>\n<option value=\\\"31536000\\\">1 年以来</option>\n</select></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">输出方式：</td>\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"3\\\"><input type=\\\"radio\\\" name=\\\"dispmode\\\" value=\\\"nav\\\" $navcheck> 页码导航 - 统计全部匹配结果 &nbsp; &nbsp;<input type=\\\"radio\\\" name=\\\"dispmode\\\" value=\\\"gradual\\\" $gradcheck> 渐进链接 - 由链接转入后续结果</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"searchsubmit\\\" value=\\\"搜  索\\\"></center>\n</form>');
