INSERT INTO cdb_templates VALUES('63','member_reg','0','<form method=\\\"post\\\" action=\\\"member.php?action=reg&referer=$referer\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">注册 - 以下项目必填</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">用户名：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"username\\\" size=\\\"25\\\" maxlength=\\\"25\\\"></td>\n</tr>\n\n$pwtd\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">Email：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"email\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">注册 - 以下项目可选</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">性别：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"1\\\"> 男 &nbsp; \n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"2\\\"> 女 &nbsp; \n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"0\\\" checked> 不告诉你\n</td></tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">生日：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"text\\\" name=\\\"year\\\" size=\\\"4\\\"> 年 \n<select name=\\\"month\\\">\n<option value=\\\"\\\">&nbsp;</option>\n<option value=\\\"1\\\">1</option>\n<option value=\\\"2\\\">2</option>\n<option value=\\\"3\\\">3</option>\n<option value=\\\"4\\\">4</option>\n<option value=\\\"5\\\">5</option>\n<option value=\\\"6\\\">6</option>\n<option value=\\\"7\\\">7</option>\n<option value=\\\"8\\\">8</option>\n<option value=\\\"9\\\">9</option>\n<option value=\\\"10\\\">10</option>\n<option value=\\\"11\\\">11</option>\n<option value=\\\"12\\\">12</option>\n</select> 月 $dayselect 日\n</td></tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">来自：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"locationnew\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">网址：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"site\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">OICQ：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"oicq\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">ICQ：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"icq\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">Yahoo：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"yahoo\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">MSN：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"msn\\\" size=\\\"25\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">个人简介：</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"5\\\" cols=\\\"30\\\" name=\\\"bio\\\"></textarea></td>\n</tr>\n\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">注册 - 论坛个性化设置</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">界面方案：</td>\n<td bgcolor=\\\"$altbg2\\\">$themelist</td>\n</tr>\n\n$changecode\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">每页主题数：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"tppnew\\\" value=\\\"$topicperpage\\\" size=\\\"4\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">每页贴数：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"pppnew\\\" value=\\\"$postperpage\\\" size=\\\"4\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">时间格式：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"radio\\\" value=\\\"24\\\" name=\\\"timeformatnew\\\">24 小时制 <input type=\\\"radio\\\" value=\\\"12\\\" name=\\\"timeformatnew\\\" checked=\\\"checked\\\">12 小时制</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">日期格式(yyyy/mm/dd，mm/dd/yy，等)：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"dateformatnew\\\" size=\\\"25\\\" value=\\\"$dateformatorig\\\"></td>\n</tr>\n\n$customstatus\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">其他选项：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"checkbox\\\" name=\\\"showemail\\\" value=\\\"1\\\" checked=\\\"checked\\\"> Email 地址可见<br>\n<input type=\\\"checkbox\\\" name=\\\"newsletter\\\" value=\\\"1\\\" checked=\\\"checked\\\"> 允许接收论坛通知 (Email 或短消息)<br>\n<input type=\\\"text\\\" name=\\\"timeoffsetnew\\\" value=\\\"$timeoffset\\\" size=\\\"3\\\"> 时间较正(北京时间 +8)，目前GMT标准时间 $currdate<br></td>\n</tr>\n\n$avatarselect\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">个人签名{$allowmaxsigsize}：<br><br>\n<a href=\\\"faq.php?page=misc#1\\\" target=\\\"_blank\\\">Discuz! 代码</a> 状态 $bbcodeis<br>\n[img]代码 状态 $imgcodeis</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"4\\\" cols=\\\"30\\\" name=\\\"sig\\\"></textarea></td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"regsubmit\\\" value=\\\"用 户 注 册\\\"></center>\n</form>');
