INSERT INTO cdb_templates VALUES('66','member_viewpro','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr><td colspan=\\\"2\\\" class=\\\"header\\\">$member 的个人资料</td>\n</tr><tr><td bgcolor=\\\"$altbg2\\\">\n\n\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"6\\\" width=\\\"98%\\\">\n<tr><td width=\\\"70%\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"$tablespace\\\" width=\\\"98%\\\">\n<tr><td colspan=\\\"2\\\" align=\\\"center\\\"><a href=\\\"###\\\" onclick=\\\"Popup(\\\'u2u.php?action=send&username=$encodeuser&sid=$sid\\\', \\\'u2u\\\', 550, 450);\\\">[ 发短消息 ]</a> \n&nbsp; &nbsp; <a href=\\\"###\\\" onclick=\\\"Popup(\\\'buddy.php?action=add&buddy=$encodeuser&sid=$sid\\\', \\\'buddylist\\\', 250, 300);\\\">[ 加为好友 ]</a> &nbsp; &nbsp; \n<a href=\\\"misc.php?action=search&srchuname=$member&srchfid=all&srchfrom=0&searchsubmit=yes\\\">[ 搜索贴子 ]</a>$edituser<br><br></td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">注册日期：</td><td width=\\\"55%\\\">$memberinfo[regdate]</td></tr>\n$regip\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">上次访问：</td><td width=\\\"55%\\\">$lastmembervisittext</td></tr>\n<tr><td colspan=\\\"2\\\"><hr noshade size=\\\"0\\\" width=\\\"95%\\\" color=\\\"$bordercolor\\\"></td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">{$credittitle}：</td><td width=\\\"55%\\\">$memberinfo[credit] $creditunit</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">用户组：</td><td width=\\\"55%\\\">$memberinfo[status] - $memberinfo[grouptitle]</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">等级：</td><td width=\\\"55%\\\">$stars</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">发帖数：</td><td width=\\\"55%\\\">$memberinfo[postnum] (占全部贴子的 $percent%)</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">平均每日发帖：</td><td width=\\\"55%\\\">$ppd 贴</td></tr>\n<tr><td colspan=\\\"2\\\"><hr noshade size=\\\"0\\\" width=\\\"95%\\\" color=\\\"$bordercolor\\\"></td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">性别：</td><td width=\\\"55%\\\">$memberinfo[gender]</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">来自：</td><td width=\\\"55%\\\">$memberinfo[location]</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">生日：</td><td width=\\\"55%\\\">$memberinfo[bday]</td></tr>\n<tr><td width=\\\"45%\\\" class=\\\"bold\\\">个人简介：</td><td width=\\\"55%\\\">$memberinfo[bio]</td></tr>\n</table>\n\n</td><td width=\\\"30%\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"100%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><td colspan=\\\"2\\\">个人信息</td></tr>\n<tr bgcolor=\\\"$altbg2\\\"><td align=\\\"center\\\" colspan=\\\"2\\\"><br>$avatar<br><br></td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">主页：</td><td bgcolor=\\\"$altbg2\\\"><a href=\\\"$memberinfo[site]\\\" target=\\\"_blank\\\">$memberinfo[site]</a></td></tr>\n$emailblock\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">OICQ：</td><td bgcolor=\\\"$altbg2\\\">$memberinfo[oicq]</td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">ICQ：</td><td bgcolor=\\\"$altbg2\\\">$memberinfo[icq]</td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">Yahoo：</td><td bgcolor=\\\"$altbg2\\\">$memberinfo[yahoo]</td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">MSN：</td><td bgcolor=\\\"$altbg2\\\">$memberinfo[msn]</td></tr>\n</table></td></tr></table>\n\n</td></tr></table>\n\n\n</td></tr></table></td></tr></table>');
