INSERT INTO cdb_templates VALUES('73','member_list','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td class=\\\"multi\\\">$multipage</td></tr>\n<tr><td height=\\\"3\\\"></td></tr></table>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td width=\\\"15%\\\" align=\\\"center\\\">用户名</td>\n<td width=\\\"6%\\\" align=\\\"center\\\">性别</td>\n<td width=\\\"8%\\\" align=\\\"center\\\">Email</td>\n<td width=\\\"8%\\\" align=\\\"center\\\">主页</td>\n<td width=\\\"14%\\\" align=\\\"center\\\">来自</td>\n<td width=\\\"10%\\\" align=\\\"center\\\">注册日期</td>\n<td width=\\\"16%\\\" align=\\\"center\\\">上次访问</td>\n<td width=\\\"8%\\\" align=\\\"center\\\">发帖</td>\n<td width=\\\"10%\\\" align=\\\"center\\\">$credittitle</td>\n</tr>\n$members\n<form method=\\\"post\\\" action=\\\"member.php?action=list\\\">\n<tr>\n<td bgcolor=\\\"$altbg2\\\" colspan=\\\"9\\\">\n搜索用户 <input type=\\\"text\\\" size=\\\"15\\\" name=\\\"srchmem\\\"> <input type=\\\"submit\\\" value=\\\"执行\\\">\n\n&nbsp;&nbsp;&nbsp;&nbsp;<span class=\\\"bold\\\">或</span>&nbsp;&nbsp;&nbsp;&nbsp;\n用户信息以：\n<a href=\\\"member.php?action=list&order=credit&desc=desc\\\">$credittitle</a> -\n<a href=\\\"member.php?action=list&order=username\\\">字母顺序</a> -\n<a href=\\\"member.php?action=list\\\">注册日期</a> -\n<a href=\\\"member.php?action=list&forumleaders=010101\\\">级别头衔</a>\n排序输出\n</td></tr></form>\n\n</table>\n</td></tr></table>\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td height=\\\"3\\\"></td></tr>\n<tr><td class=\\\"multi\\\">$multipage</td></tr></table>');
INSERT INTO cdb_templates VALUES('74','memcp_profile_viewavatars','0','<form method=\\\"post\\\" action=\\\"memcp.php?action=viewavatars\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td>$multipage</td></tr>\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\" style=\\\"table-layout: fixed\\\">\n<tr>\n<td colspan=\\\"4\\\" class=\\\"header\\\">论坛头像列表</td>\n</tr>\n\n$avatarlist\n\n</table></td></tr>\n<tr><td>$multipage</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"avasubmit\\\" value=\\\"保存头像设置\\\"></center>\n</form>');
INSERT INTO cdb_templates VALUES('75','memcp_profile','0','$navbar\n<form method=\\\"post\\\" action=\\\"memcp.php?action=profile\\\" name=\\\"reg\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">编辑个人资料 - 基本项目</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">原密码：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"password\\\" name=\\\"oldpassword\\\" size=\\\"25\\\"> 如不需要更改密码，此处请留空</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">新密码：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"password\\\" name=\\\"newpassword\\\" size=\\\"25\\\"> 如不需要更改密码，此处请留空</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">Email：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"emailnew\\\" size=\\\"25\\\" value=\\\"$member[email]\\\"> $emailcheckmsg</td>\n</tr>\n\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">编辑个人资料 - 可选项目</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">性别：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"1\\\" $checkmale> 男 &nbsp; \n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"2\\\" $checkfemale> 女 &nbsp; \n<input type=\\\"radio\\\" name=\\\"gendernew\\\" value=\\\"0\\\" $checkunknown> 不告诉你\n</td></tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">生日：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"text\\\" name=\\\"year\\\" size=\\\"4\\\" value=\\\"$bday[0]\\\"> 年 \n<select name=\\\"month\\\">\n<option value=\\\"\\\" $month[0]>&nbsp;</option>\n<option value=\\\"1\\\" $month[1]>1</option>\n<option value=\\\"2\\\" $month[2]>2</option>\n<option value=\\\"3\\\" $month[3]>3</option>\n<option value=\\\"4\\\" $month[4]>4</option>\n<option value=\\\"5\\\" $month[5]>5</option>\n<option value=\\\"6\\\" $month[6]>6</option>\n<option value=\\\"7\\\" $month[7]>7</option>\n<option value=\\\"8\\\" $month[8]>8</option>\n<option value=\\\"9\\\" $month[9]>9</option>\n<option value=\\\"10\\\" $month[10]>10</option>\n<option value=\\\"11\\\" $month[11]>11</option>\n<option value=\\\"12\\\" $month[12]>12</option>\n</select> 月 $dayselect 日\n</td></tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">来自：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"locationnew\\\" size=\\\"25\\\" value=\\\"$member[location]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">主页：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"sitenew\\\" size=\\\"25\\\" value=\\\"$member[site]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">OICQ：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"oicqnew\\\" size=\\\"25\\\" value=\\\"$member[oicq]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">ICQ：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"icqnew\\\" size=\\\"25\\\" value=\\\"$member[icq]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">Yahoo：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"yahoonew\\\" size=\\\"25\\\" value=\\\"$member[yahoo]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">MSN：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"msnnew\\\" size=\\\"25\\\" value=\\\"$member[msn]\\\"/></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">个人简介：</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"5\\\" cols=\\\"30\\\" name=\\\"bionew\\\">$member[bio]</textarea></td>\n</tr>\n\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">编辑个人资料 - 论坛个性化设置</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">界面方案：</td>\n<td bgcolor=\\\"$altbg2\\\">$themelist</td>\n</tr>\n\n$changecode\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">每页主题数：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"tppnew\\\" size=\\\"4\\\" value=\\\"$member[tpp]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">每页贴数：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"pppnew\\\" size=\\\"4\\\" value=\\\"$member[ppp]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">时间格式：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"radio\\\" value=\\\"24\\\" name=\\\"timeformatnew\\\" $check24>24 小时制\n<input type=\\\"radio\\\" value=\\\"12\\\" name=\\\"timeformatnew\\\" $check12>12 小时制</td>\n</tr>\n\n$customuserstatus\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">日期格式<br>(yyyy/mm/dd，mm/dd/yy 等)：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"dateformatnew\\\" size=\\\"25\\\" value=\\\"$member[dateformat]\\\"></td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">其他选项：</td>\n<td bgcolor=\\\"$altbg2\\\">\n<input type=\\\"checkbox\\\" name=\\\"showemailnew\\\" value=\\\"1\\\" $emailchecked> Email 地址可见<br>\n<input type=\\\"checkbox\\\" name=\\\"newsletternew\\\" value=\\\"1\\\" $newschecked> 允许接收论坛通知 (Email 或短消息)<br>\n<input type=\\\"text\\\" name=\\\"timeoffsetnew\\\" size=\\\"3\\\" value=\\\"$member[timeoffset]\\\"> 时间较正 (北京时间 +8)，目前GMT标准时间 05:52 AM\n</td></tr>\n\n$avatarselect\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">个人签名{$allowmaxsigsize}：<br><br>\n<a href=\\\"faq.php?page=misc#1\\\" target=\\\"_blank\\\">Discuz! 代码</a> 状态 $bbcodeis<br>\n[img]代码 状态 $imgcodeis</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"4\\\" cols=\\\"30\\\" name=\\\"signew\\\">$member[signature]</textarea></td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"editsubmit\\\" value=\\\"编辑个人资料\\\"></center>\n</form>');
