INSERT INTO cdb_templates VALUES('92','memcp_home','0','$navbar\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">\n您好 $cdbuserss ，欢迎进入控制面板，这里提供相关的资料设定与论坛快捷功能等。\n<br><br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\"\nalign=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=4>账号信息：</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">用户名：</td>\n<td bgcolor=\\\"$altbg2\\\" width=\\\"35%\\\">$cdbuserss</td>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"15%\\\">{$credittitle}：</td>\n<td bgcolor=\\\"$altbg2\\\" width=\\\"35%\\\">$credit $creditunit</td>\n</tr>\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">头衔：</td>\n<td bgcolor=\\\"$altbg2\\\" valign=\\\"top\\\">$status</td>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">头像：</td>\n<td bgcolor=\\\"$altbg2\\\">$avatar</td>\n</tr>\n</table>\n</td></tr></table>\n</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td>\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" width=\\\"100%\\\">\n<tr>\n<td valign=\\\"top\\\" align=\\\"left\\\"><table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"100%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\" width=\\\"20%\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td class=\\\"header\\\">好友列表</td>\n</tr>\n<tr><td bgcolor=\\\"$altbg1\\\">\n<table border=