INSERT INTO cdb_templates VALUES('93','memo_home','0','<form method=\\\"post\\\" action=\\\"memo.php\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><td colspan=\\\"\\\">搜索备忘录内容</td></tr>\n<tr><td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">\n搜索 <select name=\\\"searchfrom\\\"><option value=\\\"0\\\" selected=\\\"selected\\\">全部时间</option>\n<option value=\\\"86400\\\">1 天以来</option>\n<option value=\\\"172800\\\">2 天以来</option>\n<option value=\\\"432000\\\">1 周以来</option>\n<option value=\\\"1296000\\\">1 个月以来</option>\n<option value=\\\"5184000\\\">3 个月以来</option>\n<option value=\\\"8640000\\\">6 个月以来</option>\n<option value=\\\"31536000\\\">1 年以来</option>\n</select>\n以来的 <select name=\\\"action\\\"><option value=\\\"address\\\">个人ㄑ堵�</option>\n<option value=\\\"collections\\\">网络收藏夹</option>\n<option value=\\\"notebook\\\">我的记事簿</option>\n</select> 中包含关键字\n<input type=\\\"text\\\" name=\\\"keyword\\\" size=\\\"20\\\"> 的内容\n&nbsp; &nbsp; &nbsp; <input type=\\\"submit\\\" name=\\\"searchsubmit\\\" value=\\\"执 行\\\">\n</td></tr></table></td></tr></table></form>\n\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td>\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" width=\\\"100%\\\">\n<tr>\n<td valign=\\\"top\\\" align=\\\"left\\\">\n\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"100%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\" width=\\\"35%\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"0\\\" width=\\\"100%\\\">\n<tr><td>\n<table border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><