INSERT INTO cdb_templates VALUES('103','viewthread_modoptions','0','<span class=\\\"mediumtxt\\\"><span class=\\\"bold\\\">论坛管理：</span>\n<select name=\\\"action\\\" id=\\\"action\\\" onchange=\\\"\nif(this.options[this.selectedIndex].value != \\\'\\\') { if(this.options[this.selectedIndex].value != \\\'delpost\\\') {\nwindow.location=(\\\'topicadmin.php?tid=$tid&fid=$fid&action=\\\'+this.options[this.selectedIndex].value+\\\'&sid=$sid\\\');\n} else { this.document.delpost.submit(); } }\\\">\n<option value=\\\"\\\" selected>管理选项</option>\n<option value=\\\"delpost\\\">删除贴子</option>\n<option value=\\\"delete\\\">删除主题</option>\n<option value=\\\"close\\\">$closeopen</option>\n<option value=\\\"move\\\">移动主题</option>\n<option value=\\\"digist\\\">$setdigist</option>\n<option value=\\\"top\\\">$topuntop</option>\n<option value=\\\"split\\\">分割主题</option>\n<option value=\\\"merge\\\">合并主题</option>\n<option value=\\\"bump\\\">提升主题</option>\n</select></span>');
