INSERT INTO cdb_templates VALUES('105','viewthread_printable','0','<html>\n<head>\n<style type=\\\"text/css\\\">\nbody,table,td {font-size: 15px; font-family: Times New Roman, Arial, 宋体}\n</style>\n<title>$bbname - Powered by Discuz! Board</title>\n</head>\n<script language=javascript>\nfunction print(){\n        if (document.layers) {\n                window.print() ;  \n        } else {\n                var WebBrowser = \\\'<OBJECT ID=\\\"WebBrowser1\\\" WIDTH=0 HEIGHT=0 CLASSID=\\\"CLSID:8856F961-340A-11D0-A96B-00C04FD705A2\\\"></OBJECT>\\\';\n                document.body.insertAdjacentHTML(\\\'beforeEnd\\\', WebBrowser);\n                WebBrowser1.ExecWB(6, 2);\n                WebBrowser1.outerHTML = \\\'\\\';\n        }\n}\n</script>\n<body>\n\n<img src=\\\"$imgdir/$boardimg\\\" alt=\\\"Board logo\\\" border=\\\"0\\\"><br><br>\n<span class=\\\"bold\\\">标题：$thread[subject] <a href=\\\"javascript:print();\\\">[打印]</a></span><br>\n$posts\n<hr noshade size=\\\"0\\\" width=\\\"95%\\\" color=\\\"$bordercolor\\\"><table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"95%\\\" align=\\\"center\\\">\n<tr><td style=\\\"font-family: Tahoma,Verdana,宋体; font-size: 12px\\\">欢迎光临 $bbname ($boardurl)</td>\n<td align=\\\"right\\\" style=\\\"font-family: Tahoma,Verdana,宋体; font-size: 12px\\\">\nPowered by Discuz! $version</td></tr></table>\n</body></html>');
