INSERT INTO cdb_templates VALUES('107','post_preview','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><td colspan=\\\"2\\\">预览贴子</td></tr>\n<tr bgcolor=\\\"$altbg1\\\">\n<td rowspan=\\\"2\\\" valign=\\\"top\\\" width=\\\"20%\\\"><span class=\\\"bold\\\">$username</span><br><br></td>\n<td>$thread[icon] $poston</td></tr>\n<tr bgcolor=\\\"$altbg1\\\"><td>\n<table height=\\\"100%\\\" width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" style=\\\"table-layout: fixed; word-wrap: break-word\\\">\n<tr><td><span class=\\\"bold\\\">$dissubject</span><br><br>$message1<br></td></tr></table></td></tr>\n\n</table>\n</td></tr></table>\n<br>');
INSERT INTO cdb_templates VALUES('108','post_newthread','0','<script language=\\\"JavaScript\\\" src=\\\"bbcode.js\\\"></script>\n<script language=\\\"JavaScript\\\">\nvar postmaxchars = $maxpostsize;\nvar isadmin = $isadmin;\nfunction checklength(theform) {\n        if (postmaxchars != 0) { message = \\\"系统允许：$maxpostsize 字符\\\"; }\n        else { message = \\\"\\\"; }\n        alert(\\\"\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n\\\\n\\\"+message);\n}\nfunction validate(theform) {\n        if (theform.subject.value == \\\"\\\") {\n                alert(\\\"请完成标题和内容栏。\\\");\n                return false; }\n        if (postmaxchars != 0 && isadmin != 1) {\n                if (theform.message.value.length > $maxpostsize) {\n                        alert(\\\"您的贴子长度超过限制\\\\n\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n系统允许：$maxpostsize 字符\\\");\n                        return false; }\n                else { return true; }\n        } else { return true; }\n}\n</script>\n$preview\n<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"post.php?action=newthread&fid=$fid\\\" $enctype onSubmit=\\\"return validate(this)\\\">\n<input type=\\\"hidden\\\" name=\\\"topicsubmit\\\" value=\\\"submit\\\">\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">发新话题</td>\n</tr>\n\n$loggedin\n$viewpermission\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">主题：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"45\\\" value=\\\"$subject\\\" tabindex=\\\"3\\\"></td>\n</tr>\n<tr><td bgcolor=\\\"$altbg1\\\">图标：</td><td bgcolor=\\\"$altbg2\\\">$icons</td></tr>\n$bbcodeinsert\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">贴子内容 <a href=\\\"javascript:checklength(document.input);\\\">[字数检查]</a>：<br><br>\nhtml 状态 $allowhtml<br>\nSmilies 状态 $allowsmilies<br>\n<a href=\\\"faq.php?page=misc#1\\\" target=\\\"_blank\\\">Discuz! 代码</a> 状态 $allowbbcode<br>\n[img]代码 状态 $allowimgcode\n<br><br><br><br>\n$smilieinsert\n</td>\n\n<td align=\\\"left\\\" bgcolor=\\\"$altbg2\\\">\n<textarea rows=\\\"18\\\" cols=\\\"85\\\" name=\\\"message\\\" tabindex=\\\"4\\\" onSelect=\\\"javascript: storeCaret(this);\\\" onClick=\\\"javascript: storeCaret(this);\\\" onKeyUp=\\\"javascript: storeCaret(this);\\\" onKeyDown=\\\"javascript: ctlent();\\\">$message</textarea>\n<br><br>\n<input type=\\\"checkbox\\\" name=\\\"parseurloff\\\" value=\\\"1\\\"> 禁用 URL 识别<br>\n<input type=\\\"checkbox\\\" name=\\\"smileyoff\\\" value=\\\"1\\\" $smileyoffcheck> 禁用 Smilies<br>\n<input type=\\\"checkbox\\\" name=\\\"bbcodeoff\\\" value=\\\"1\\\" $codeoffcheck> 禁用 Discuz! 代码<br>\n<input type=\\\"checkbox\\\" name=\\\"usesig\\\" value=\\\"1\\\" $usesigcheck> 使用个人签名<br>\n<input type=\\\"checkbox\\\" name=\\\"emailnotify\\\" value=\\\"1\\\"> 接收新回复邮件通知\n$modoptions</td> \n</tr>\n$attachfile\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"topicsubmit\\\" value=\\\"发表贴子\\\" tabindex=\\\"5\\\">\n<input type=\\\"submit\\\" name=\\\"previewpost\\\" value=\\\"预览贴子\\\" tabindex=\\\"6\\\">\n</center>\n</form>');
INSERT INTO cdb_templates VALUES('109','topicadmin_getip','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"60%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n<form method=\\\"post\\\" action=\\\"admincp.php?action=ipban&showmsgtype=cdb\\\">\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n\n<tr>\n<td class=\\\"header\\\" colspan=\\\"3\\\">查看 IP</td>\n</tr>\n<tr bgcolor=\\\"$altbg2\\\">\n<td align=\\\"center\\\">此用户的 IP 地址是 <span class=\\\"bold\\\">$useip</span> - $iplocation\n</td>\n<tr bgcolor=\\\"$altbg1\\\"><td align=\\\"center\\\">\n$ipban\n</td></tr>\n</table></td></tr></table></form>');
INSERT INTO cdb_templates VALUES('110','topicadmin_delthread','0','<form method=\\\"post\\\" action=\\\"topicadmin.php?action=delthread\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"2\\\">删除选定主题</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">用户名：</td>\n<td bgcolor=\\\"$altbg2\\\">$cdbuserss  [<a href=\\\"member.php?action=logout\\\">退出登录</a>]</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n$deleteid\n<input type=\\\"hidden\\\" name=\\\"page\\\" value=\\\"$page\\\">\n<input type=\\\"hidden\\\" name=\\\"fid\\\" value=\\\"$fid\\\">\n<center><input type=\\\"submit\\\" name=\\\"delthreadsubmit\\\" value=\\\"删除主题\\\"></center>\n</form>');
