INSERT INTO cdb_templates VALUES('113','topicadmin_digist','0','<form method=\\\"post\\\" action=\\\"topicadmin.php?action=digist\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"2\\\">$cdbaction</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">用户名：</td>\n<td bgcolor=\\\"$altbg2\\\">$cdbuserss  [<a href=\\\"member.php?action=logout\\\">退出登录</a>]</td>\n</tr>\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">精华级别：</td>\n<td bgcolor=\\\"$altbg2\\\">\n$removedigist\n<input type=\\\"radio\\\" name=\\\"level\\\" value=\\\"1\\\" $digistcheck> <img src=\\\"$imgdir/star.gif\\\"> &nbsp; &nbsp; \n<input type=\\\"radio\\\" name=\\\"level\\\" value=\\\"2\\\"> <img src=\\\"$imgdir/star.gif\\\"><img src=\\\"$imgdir/star.gif\\\"> &nbsp; &nbsp; \n<input type=\\\"radio\\\" name=\\\"level\\\" value=\\\"3\\\"> <img src=\\\"$imgdir/star.gif\\\"><img src=\\\"$imgdir/star.gif\\\"><img src=\\\"$imgdir/star.gif\\\"></td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<input type=\\\"hidden\\\" name=\\\"fid\\\" value=\\\"$fid\\\">\n<input type=\\\"hidden\\\" name=\\\"tid\\\" value=\\\"$tid\\\">\n<center><input type=\\\"submit\\\" name=\\\"digistsubmit\\\" value=\\\"$cdbaction\\\"></center>\n</form>');
