INSERT INTO cdb_templates VALUES('119','topicadmin_split','0','<form method=\\\"post\\\" action=\\\"topicadmin.php?action=split\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td class=\\\"header\\\" colspan=\\\"2\\\">分割主题</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">用户名</td>\n<td bgcolor=\\\"$altbg2\\\">$cdbuserss  [<a href=\\\"member.php?action=logout\\\">退出登录</a>]</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">新主题的标题：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"45\\\"></td>\n</tr>\n\n<tr class=\\\"header\\\">\n<td>分割</td>\n<td>内容</td>\n</tr>\n$posts\n</table>\n</td></tr></table><br>\n<input type=\\\"hidden\\\" name=\\\"fid\\\" value=\\\"$fid\\\">\n<input type=\\\"hidden\\\" name=\\\"tid\\\" value=\\\"$tid\\\">\n<center><input type=\\\"submit\\\" name=\\\"splitsubmit\\\" value=\\\"分割主题\\\"></center>\n</form>');
