INSERT INTO cdb_templates VALUES('121','topicadmin_merge','0','<form method=\\\"post\\\" action=\\\"topicadmin.php?action=merge\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td class=\\\"header\\\" colspan=\\\"2\\\">合并主题</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"21%\\\">用户名：</td>\n<td bgcolor=\\\"$altbg2\\\">$cdbuserss  [<a href=\\\"member.php?action=logout\\\">退出登录</a>]</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\">主题 tid：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"othertid\\\" size=\\\"10\\\">\n&nbsp;&nbsp;&nbsp;即将与这个主题合并的 tid，如 viewthread.php?tid=48，tid 为 48</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<input type=\\\"hidden\\\" name=\\\"fid\\\" value=\\\"$fid\\\">\n<input type=\\\"hidden\\\" name=\\\"tid\\\" value=\\\"$tid\\\">\n<center><input type=\\\"submit\\\" name=\\\"mergesubmit\\\" value=\\\"合并主题\\\"></center>\n</form>');
INSERT INTO cdb_templates VALUES('122','topicadmin_report','0','<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"topicadmin.php?action=report\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"2\\\">向版主反应这个贴子</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\" width=\\\"19%\\\">我对这个贴子有异议，我认为：</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"9\\\" cols=\\\"45\\\" name=\\\"reason\\\"></textarea>\n</tr>\n\n</table>\n</td></tr></table><br>\n<input type=\\\"hidden\\\" name=\\\"tid\\\" value=\\\"$tid\\\">\n<input type=\\\"hidden\\\" name=\\\"fid\\\" value=\\\"$fid\\\">\n<input type=\\\"hidden\\\" name=\\\"pid\\\" value=\\\"$pid\\\">\n<center><input type=\\\"submit\\\" name=\\\"reportsubmit\\\" value=\\\"报告此贴\\\"></center>\n</form>');
INSERT INTO cdb_templates VALUES('123','showmessage','0','<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"400\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\"><table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\"><td>$bbname 提示</td></tr><tr><td bgcolor=\\\"$altbg2\\\" align=\\\"center\\\">\n<table border=\\\"0\\\" width=\\\"90%\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\"><tr><td width=\\\"100%\\\" align=\\\"center\\\">\n$show_message\n</td></tr></table></td></tr></table></td></tr></table><br><br>');
INSERT INTO cdb_templates VALUES('124','forumdisplay_password','0','<form method=\\\"post\\\" action=\\\"$url\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"50%\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"2\\\">本论坛需要密码，您必须在下面输入正确的密码才能浏览这个论坛。</td>\n</tr>\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" align=\\\"center\\\">密码：</td>\n<td bgcolor=\\\"$altbg2\\\" align=\\\"center\\\"><input type=\\\"password\\\" name=\\\"pw\\\" size=\\\"25\\\"> <br></td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"loginsubmit\\\" value=\\\"登录\\\"></center>\n</form>');
INSERT INTO cdb_templates VALUES('125','member_reg_rules','0','<form name=\\\"bbrules\\\" method=\\\"post\\\" action=\\\"member.php?action=reg&referer=$referer\\\">\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td class=\\\"header\\\">注册</td>\n</tr>\n\n<tr bgcolor=\\\"$altbg1\\\">\n<td width=\\\"21%\\\">$bbrulestxt</td>\n</tr>\n\n</table>\n</td></tr></table><br>\n<center>\n<input type=\\\"submit\\\" name=\\\"rulesubmit\\\" value=\\\"同  意\\\" style=\\\"height: 23px\\\">\n<input type=\\\"button\\\" name=\\\"return\\\" value=\\\"不同意\\\" style=\\\"height: 23px\\\" onclick=\\\"javascript:history.go(-1);\\\">\n</form>\n</center>\n\n<script language=\\\"javascript\\\">\nvar secs = 9;\nvar wait = secs * 1000;\ndocument.bbrules.rulesubmit.value = \\\"同意(\\\" + secs + \\\")\\\";\ndocument.bbrules.rulesubmit.disabled = true;\nfor(i = 1; i <= secs; i++) {\n        window.setTimeout(\\\"update(\\\" + i + \\\")\\\", i * 1000);\n}\nwindow.setTimeout(\\\"timer()\\\", wait);\nfunction update(num, value) {\n        if(num == (wait/1000)) {\n                document.bbrules.rulesubmit.value = \\\"同意\\\";\n        } else {\n                printnr = (wait / 1000)-num;\n                document.bbrules.rulesubmit.value = \\\"同意(\\\" + printnr + \\\")\\\";\n        }\n}\nfunction timer() {\n        document.bbrules.rulesubmit.disabled = false;\n        document.bbrules.rulesubmit.value = \\\"同  意\\\";\n}\n</script>');
INSERT INTO cdb_templates VALUES('126','post_newpoll','0','<script language=\\\"JavaScript\\\" src=\\\"bbcode.js\\\"></script>\n<script language=\\\"JavaScript\\\">\nvar postmaxchars = $maxpostsize;\nvar isadmin = \\\"$isadmin\\\";\nfunction checklength(theform) {\n        if (postmaxchars != 0) { message = \\\"系统允许：$maxpostsize 字符\\\"; }\n        else { message = \\\"\\\"; }\n        alert(\\\"\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n\\\\n\\\"+message);\n}\nfunction validate(theform) {\n        if (theform.subject.value == \\\"\\\") {\n                alert(\\\"请完成标题和内容栏。\\\");\n                return false; }\n        if (postmaxchars != 0 && isadmin != 1) {\n                if (theform.message.value.length > $maxpostsize) {\n                        alert(\\\"您的贴子长度超过限制\\\\n\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n系统允许：$maxpostsize 字符\\\");\n                        return false; }\n                else { return true; }\n        } else { return true; }\n}\n</script>\n$preview\n<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"post.php?action=newthread&fid=$fid&poll=yes\\\" $enctype onSubmit=\\\"return validate(this)\\\">\n<input type=\\\"hidden\\\" name=\\\"topicsubmit\\\" value=\\\"submit\\\">\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">发起投票</td>\n</tr>\n\n$loggedin\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">主题：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"45\\\" value=\\\"$subject\\\" tabindex=\\\"3\\\"></td>\n</tr>\n<tr><td bgcolor=\\\"$altbg1\\\">图标：</td><td bgcolor=\\\"$altbg2\\\">$icons</td></tr>\n$bbcodeinsert\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">投票选项：<br>（每个选项一行，最多十行）</td>\n<td bgcolor=\\\"$altbg2\\\"><textarea rows=\\\"4\\\" cols=\\\"85\\\" name=\\\"pollanswers\\\" tabindex=\\\"5\\\">$pollanswers</textarea></td>\n</tr>\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">贴子内容 <a href=\\\"javascript:checklength(document.input);\\\">[字数检查]</a>：<br><br>\nhtml 状态 $allowhtml<br>\nSmilies 状态 $allowsmilies<br>\n<a href=\\\"faq.php?page=misc#1\\\" target=\\\"_blank\\\">Discuz! 代码</a> 状态 $allowbbcode<br>\n[img]代码状态 $allowimgcode\n<br><br><br><br>\n$smilieinsert\n</td>\n<td bgcolor=\\\"$altbg2\\\">\n<textarea rows=\\\"18\\\" cols=\\\"85\\\" name=\\\"message\\\" tabindex=\\\"5\\\" onSelect=\\\"javascript: storeCaret(this);\\\" onClick=\\\"javascript: storeCaret(this);\\\" onKeyUp=\\\"javascript: storeCaret(this);\\\" onKeyDown=\\\"javascript: ctlent();\\\">$message</textarea>\n<br><br>\n<input type=\\\"checkbox\\\" name=\\\"parseurloff\\\" value=\\\"1\\\"> 禁用 URL 识别<br>\n<input type=\\\"checkbox\\\" name=\\\"smileyoff\\\" value=\\\"1\\\" $smileyoffcheck> 禁用 Smilies<br>\n<input type=\\\"checkbox\\\" name=\\\"bbcodeoff\\\" value=\\\"1\\\" $codeoffcheck> 禁用 Discuz! 代码<br>\n<input type=\\\"checkbox\\\" name=\\\"usesig\\\" value=\\\"1\\\" $usesigcheck> 使用个人签名<br>\n<input type=\\\"checkbox\\\" name=\\\"emailnotify\\\" value=\\\"1\\\"> 接收新回复邮件通知\n$modoptions</td> \n</tr>\n$attachfile\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"topicsubmit\\\" value=\\\"发布投票\\\" tabindex=\\\"6\\\">\n<input type=\\\"submit\\\" name=\\\"previewpost\\\" value=\\\"预览投票\\\" tabindex=\\\"7\\\">\n</center>\n</form>');
INSERT INTO cdb_templates VALUES('127','post_reply','0','<script language=\\\"JavaScript\\\" src=\\\"bbcode.js\\\"></script>\n<script language=\\\"JavaScript\\\">\nvar postmaxchars = $maxpostsize;\nvar isadmin = \\\"$isadmin\\\";\nfunction checklength(theform) {\n        if (postmaxchars != 0) { message = \\\"系统允许：$maxpostsize 字符\\\"; }\n        else { message = \\\"\\\"; }\n        alert(\\\"\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n\\\\n\\\"+message);\n}\nfunction validate(theform) {\n        if (theform.message.value == \\\"\\\" && theform.subject.value == \\\"\\\") {\n                alert(\\\"请完成标题或内容栏。\\\");\n                return false; }\n        if (postmaxchars != 0 && isadmin != 1) {\n                if (theform.message.value.length > $maxpostsize) {\n                        alert(\\\"您的贴子长度超过限制\\\\n\\\\n当前长度：\\\"+theform.message.value.length+\\\" 字符\\\\n系统允许：$maxpostsize 字符\\\");\n                        return false; }\n                else { return true; }\n        } else { return true; }\n}\n</script>\n$preview\n<form method=\\\"post\\\" name=\\\"input\\\" action=\\\"post.php?action=reply&fid=$fid&tid=$tid\\\" $enctype onSubmit=\\\"return validate(this)\\\">\n<input type=\\\"hidden\\\" name=\\\"replysubmit\\\" value=\\\"submit\\\">\n\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"$borderwidth\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr>\n<td colspan=\\\"2\\\" class=\\\"header\\\">回复主题</td>\n</tr>\n\n$loggedin\n\n<tr>\n<td bgcolor=\\\"$altbg1\\\" width=\\\"20%\\\">主题：</td>\n<td bgcolor=\\\"$altbg2\\\"><input type=\\\"text\\\" name=\\\"subject\\\" size=\\\"45\\\" value=\\\"$subject\\\" tabindex=\\\"3\\\">&nbsp;&nbsp;(可选)</td>\n</tr>\n<tr><td bgcolor=\\\"$altbg1\\\">图标：</td><td bgcolor=\\\"$altbg2\\\">$icons</td></tr>\n$bbcodeinsert\n<tr>\n<td bgcolor=\\\"$altbg1\\\" valign=\\\"top\\\">贴子内容 <a href=\\\"javascript:checklength(document.input);\\\">[字数检查]</a>：<br><br>\nhtml 状态 $allowhtml<br>\nSmilies 状态 $allowsmilies<br>\n<a href=\\\"faq.php?page=misc#1\\\" target=\\\"_blank\\\">Discuz! 代码</a> 状态 $allowbbcode<br>\n[img]代码 状态 $allowimgcode\n<br><br><br><br>\n$smilieinsert\n</td>\n\n<td bgcolor=\\\"$altbg2\\\">\n<textarea rows=\\\"18\\\" cols=\\\"85\\\" name=\\\"message\\\" tabindex=\\\"4\\\" onSelect=\\\"javascript: storeCaret(this);\\\" onClick=\\\"javascript: storeCaret(this);\\\" onKeyUp=\\\"javascript: storeCaret(this);\\\" onKeyDown=\\\"javascript: ctlent();\\\">$message</textarea>\n<br><br>\n<input type=\\\"checkbox\\\" name=\\\"parseurloff\\\" value=\\\"1\\\"> 禁用 URL 识别<br>\n<input type=\\\"checkbox\\\" name=\\\"smileyoff\\\" value=\\\"1\\\" $smileyoffcheck> 禁用 Smilies<br>\n<input type=\\\"checkbox\\\" name=\\\"bbcodeoff\\\" value=\\\"1\\\" $codeoffcheck> 禁用 Discuz! 代码<br>\n<input type=\\\"checkbox\\\" name=\\\"usesig\\\" value=\\\"1\\\" $usesigcheck> 使用个人签名<br>\n<input type=\\\"checkbox\\\" name=\\\"emailnotify\\\" value=\\\"1\\\"> 接收新回复邮件通知\n</td> \n</tr>\n$attachfile\n</table>\n</td></tr></table><br>\n<center><input type=\\\"submit\\\" name=\\\"replysubmit\\\" value=\\\"发表回复\\\" tabindex=\\\"5\\\">\n<input type=\\\"submit\\\" name=\\\"previewpost\\\" value=\\\"预览贴子\\\" tabindex=\\\"6\\\">\n</center>\n</form>\n\n<br>\n<table cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" border=\\\"0\\\" width=\\\"$tablewidth\\\" align=\\\"center\\\">\n<tr><td bgcolor=\\\"$bordercolor\\\">\n\n<table border=\\\"0\\\" cellspacing=\\\"1\\\" cellpadding=\\\"$tablespace\\\" width=\\\"100%\\\">\n<tr class=\\\"header\\\">\n<td colspan=\\\"2\\\">主题回顾</td>\n</tr>\n$posts\n</table>\n</td></tr></table>');
INSERT INTO cdb_templates VALUES('128','post_reply_review_post','0','<tr bgcolor=\\\"$thisbg\\\">\n<td rowspan=\\\"2\\\" valign=\\\"top\\\" width=\\\"20%\\\"><span class=\\\"bold\\\">$post[author]</span><br><br></td>\n<td>$post[icon] $poston</td></tr>\n<tr bgcolor=\\\"$thisbg\\\"><td>\n<table height=\\\"100%\\\" width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" style=\\\"table-layout: fixed; word-wrap: break-word\\\">\n<tr><td><p>$post[message]</p><br></td></tr></table></td></tr>');
